"use client";
import { AuthContext } from "@/Context/authContext";
import Image from "next/image";
import Link from "next/link";
import React, { useContext, useEffect, useState } from "react";
import GoogleButton from "react-google-button";

const AuthBox = ({}) => {
  const [isSignup, setIsSignup] = useState(false);
  let signupForm={
    name: "",
    email: "",
    password: "",
    cpassword: "",
  }
  let loginForm={
    name: "",
    email: "",
    password: "",
  }
  const [sform, setSform] = useState(signupForm);

  const [lform, setLform] = useState(loginForm);

  const {
    GoogleSignIn,
    handleSigninWithEmailAndPass,
    createUser,
    handleSignout,
    data,
  } = useContext(AuthContext);

  const toggleAuthMode = () => {
    setIsSignup((prevIsSignup) => !prevIsSignup);
  };

  const handleSChange = (e) => {
    setSform({ ...sform, [e.target.name]: e.target.value });
  };
  const handleLChange = (e) => {
    setLform({ ...lform, [e.target.name]: e.target.value });
  };

  const submit = async (event) => {
    event.preventDefault();
    if (isSignup) {
      try {
        await createUser(sform);
        setSform(signupForm)
      } catch (error) {
      }
    } else {
      try {
        handleSigninWithEmailAndPass(lform);
        setLform(loginForm)
      } catch (error) {
      }
    }
  };
  useEffect(() => {
    // console.log(data);
  }, [data]);

  return (
    <div className="w-[400px] p-6 my-36 mx-auto bg-white border rounded-lg shadow-lg">
      <h2 className="mb-6 text-2xl text-center">
        {data ? "Profile" : isSignup ? "Sign Up" : "Log In"}
      </h2>
      {data ? (
        <div className="flex flex-col justify-center text-center">
          <div className="flex items-center justify-around py-8">
            <Image
              src={
                data?.photoURL
                  ? data.photoURL
                  : "data:image/png;base64,"
              }
              width={50}
              height={50}
            ></Image>
            <div className="flex flex-col items-start justify-around">
              <h1>Name: {data?.displayName}</h1>
              <h1>Email: {data?.email}</h1>
            </div>
          </div>

          <button
            className="p-3 bg-blue-500 text-white rounded"
            onClick={handleSignout}
          >
            {" "}
            Logout{" "}
          </button>
          <p className="text-center text-black-500 py-4 ">Welcome to  <p  className="font-bold inline cursor-pointer"><Link href="/">Places:Flavours Of Bangalore</Link></p></p>
        </div>
      ) : (
        <form className="flex flex-col" onSubmit={submit}>
          {isSignup ? (
            <>
              <input
                className="mb-4 p-2 border rounded"
                type="text"
                name="name"
                onChange={(e) => handleSChange(e)}
                value={sform.name}
                placeholder="Full Name"
                required
              />
              <input
                className="mb-4 p-2 border rounded"
                type="email"
                name="email"
                onChange={(e) => handleSChange(e)}
                value={sform.email}
                placeholder="Email"
                required
              />
              <input
                className="mb-4 p-2 border rounded"
                type="password"
                name="password"
                onChange={(e) => handleSChange(e)}
                value={sform.password}
                placeholder="Password"
                required
              />
              <input
                className="mb-4 p-2 border rounded"
                type="password"
                name="cpassword"
                onChange={(e) => handleSChange(e)}
                value={sform.cpassword}
                placeholder="Confirm Password"
                required
              />
            </>
          ) : (
            <>
              <input
                className="mb-4 p-2 border rounded"
                type="text"
                name="email"
                onChange={(e) => handleLChange(e)}
                value={lform.email}
                placeholder="Email"
              />
              <input
                className="mb-4 p-2 border rounded"
                type="password"
                name="password"
                onChange={(e) => handleLChange(e)}
                value={lform.password}
                placeholder="Password"
              />
            </>
          )}
          <button className="p-3 bg-blue-500 text-white rounded" type="submit">
            {isSignup ? "Sign Up" : "Log In"}{" "}
          </button>
          {!isSignup && (
            <div style={{ width: "100%", margin: "auto" }} className="pt-3">
              <GoogleButton style={{ width: "350px" }} onClick={GoogleSignIn} />
            </div>
          )}
          <p
            className="text-center text-blue-500 cursor-pointer py-4"
            onClick={toggleAuthMode}
          >
            {isSignup
              ? "Already have an account? Log In"
              : "Don't have an account? Sign Up"}
          </p>
        </form>
      )}
      <div className="flex justify-center mt-4"></div>
    </div>
  );
};

export default AuthBox;
